#!/usr/bin/env python3

import sys
import argparse
import re
from Bio.Align import PairwiseAligner
import random
import base64

INCORRECT_MESSAGES = [
    "Womp-womp. Try again! ðŸ˜¿",
    "Somebody dun goofed! ðŸ‘»",
    "I'm not mad, I'm just disappointed. ðŸ§",
    "Closeâ€¦ but also very, very far. ðŸ¤­",
    "Swing and a miss! âš¾âŒ",
    "You'll get it next time! â€¦Probably. ðŸ˜„ðŸ¤ž"
]

truth_data = b''

def parseArgs():
    parser = argparse.ArgumentParser(description='This script takes in files from each of the participants and compiles them into a sequence.')
    parser.add_argument('files', nargs='+', help='The files to use for the challenge')
    return parser.parse_args()

def natural_sort_key(s):
    parts = re.split(r'(\d+)', s)
    return [int(part) if part.isdigit() else part for part in parts]

def load_truth(data):
    t = base64.b64decode(data)
    t = t.decode('utf-8')
    t = t.replace('\n','')
    return t

def align(seq1: str, seq2: str, match=1, mismatch=-1, open_gap=-1, extend_gap=-1):
    seq1 = seq1.strip().upper()
    seq2 = seq2.strip().upper()

    aligner = PairwiseAligner()
    aligner.mode = "global"
    aligner.match_score = match
    aligner.mismatch_score = mismatch
    aligner.open_gap_score = open_gap
    aligner.extend_gap_score = extend_gap

    alignments = aligner.align(seq1, seq2)
    best = alignments[0]

    aln1 = best[0, :]
    aln2 = best[1, :]  

    return aln1, aln2


def calc_pid(truth, query):
    aln_truth, aln_query = align(truth, query)

    matches = 0
    aligned_positions = 0
    for a, b in zip(aln_truth, aln_query):
        if a == "-" or b == "-":
            continue
        aligned_positions += 1
        if a == b:
            matches += 1

    pid = 100 * (matches / aligned_positions if aligned_positions else 0.0)

    return pid

def display_results(pid, truth, query):
    result = "â­ Yay. So proud of you. â­" if pid == 100 else random.choice(INCORRECT_MESSAGES)
    divider = '\n' + 30 * "=" + '\n'
    msg = f"""
    truth length = {len(truth)} bp
    query length = {len(query)} bp
    identity     = {pid}%

    {result}
    """
    print(divider + msg + divider)

    return True if pid == 100 else False
    
def main(args):
    # Truth sequence
    truth = load_truth(truth_data)
    
    # Sort Reads by Key
    args.files.sort(key=natural_sort_key)

    # Read data in from each file
    data = []
    for file in args.files:
        with open(file,'r') as infile:
            for line in infile:
                if line.startswith('>'):
                    continue
                data.append(line.strip())

    # Join list of data
    sequence = ''.join(data)
    # print(sequence)

    pid = calc_pid(truth, sequence)

    success = display_results(pid, truth, sequence)

    with open('result.fasta','w') as outfile:
            outfile.writelines(data)

    if success:
        sys.exit(0)
    else:
        sys.exit(1)

if __name__ == "__main__":
    args = parseArgs()
    main(args)